            }
        }
        for(Integer k: map.keySet()){
            if(map.get(k) > array.length/2){
                return k;
            }
        }
        return 0;
    }
}




29 最小的k个数


输入n个整数，找出其中最小的K个数。例如输入4,5,1,6,2,7,3,8这8个数字，则最小的4个数字是1,2,3,4,。


import java.util.ArrayList;
public class Solution {
    public ArrayList<Integer> GetLeastNumbers_Solution(int [] input, int k) {
        ArrayList<Integer> list = new ArrayList<>();
        if(input.length == 0){
            return list;
        }
        heapSort(list, input, input.length, k);
        return list;
    }

    public void heapSort(ArrayList<Integer> list, int[] input, int len, int k){
        if(len > input.length-k && k <= input.length){
            int temp;
            int index = len/2-1;
            for(int i=index; i>-1; i--){
                int remain_i = i;
                boolean exchange_flag = false;
                int min_index = -1;
                if(i*2+2 < len){
                    min_index = input[i*2+1] < input[i*2+2] ? i*2+1 : i*2+2;
                }else{
                    min_index = i*2+1;
                }

                if(input[i] > input[min_index]){
                    exchange_flag = true;
                    temp = input[i];//exchange
                    input[i] = input[min_index];
                    input[min_index] = temp;
                }
                while(exchange_flag == true){
                    i = min_index;
                    if(i*2+2 < len){
                        min_index = input[i*2+1] < input[i*2+2] ? i*2+1 : i*2+2;
                        if(input[i] > input[min_index]){
                            temp = input[i];//exchange
                            input[i] = input[min_index];
                            input[min_index] = temp;
                        }else{
                            exchange_flag = false;
                        }
                    }else if(i*2+1 < len){
                        min_index = i*2+1;
                        if(input[i] > input[min_index]){
                            temp = input[i];//exchange
                            input[i] = input[min_index];
                            input[min_index] = temp;
                        }else{
                            exchange_flag = false;
                        }
                    }else{
                        exchange_flag = false;
                    }
                }
                i = remain_i;
            }
            int min = input[0];
            input[0] = input[len-1];
            input[len-1] = min;
            list.add(min);
            heapSort(list,input,len-1,k);
        }
    }
}




