一个整型数组里除了两个数字之外，其他的数字都出现了偶数次。请写程序找出这两个只出现一次的数字。  //num1,num2分别为长度为1的数组。传出参数
//将num1[0],num2[0]设置为返回结果
public class Solution {
    public void FindNumsAppearOnce(int [] array,int num1[] , int num2[]) {
        if(array.length == 2){
            num1[0] = array[0];
            num2[0] = array[0];
        }else{
            int n = array[0];
            for(int i=1; i<array.length; i++){
                n = n ^ array[i];
            }

            int first1_index = 0;
            while((n&1) == 0 && first1_index < 32){
                n = n >> 1;
                ++first1_index;
            }

            num1[0] = 0;
            num2[0] = 0;
            for(int i=0; i<array.length; i++) {
                if (((array[i] >> first1_index) & 1) == 0) {
                    num1[0] = num1[0] ^ array[i];
                } else {
                    num2[0] = num2[0] ^ array[i];
                }
            }
        }
    }
}

