public class Solution {
    public int LastRemaining_Solution(int n, int m) {
        if(n == 0){return -1;}
        else if(n == 1){return 0;}
        LinkedList<Integer> list = new LinkedList<>();
        for(int i=0; i<n; i++){
            list.add(i);
        }
        int current_index = 0;
        while(list.size()>1){
            current_index = (current_index + m -1) % list.size();
            list.remove(current_index);
        }
        return(list.get(0));
    }
}

