        queue.offer(pRoot);
        queue.offer(null);
        boolean flag = true;
        ArrayList<Integer> current_list = new ArrayList<>();
        while(!queue.isEmpty()){
            TreeNode node = queue.poll();
            if(node!=null){
                if(flag == true){//根据flag来决定插入的顺序
